import React, { useState, useCallback } from 'react';
import { FileCode2, Globe, Copy, Download, Trash2, Check, Folder, ChevronRight, ChevronDown } from 'lucide-react';
import JSZip from 'jszip';

// File blacklist patterns
const BLACKLIST_PATTERNS = [
  // Dependencies
  'node_modules/',
  'vendor/',
  'bower_components/',
  '.pnp/',
  '.yarn/',

  // Build outputs
  'dist/',
  'build/',
  'out/',
  '.next/',
  '.nuxt/',
  '.cache/',
  'coverage/',

  // IDE & OS
  '.idea/',
  '.vscode/',
  '.DS_Store',
  'Thumbs.db',

  // Version control
  '.git/',
  '.svn/',
  '.hg/',

  // Config files we want to skip
  'package-lock.json',
  'yarn.lock',
  'pnpm-lock.yaml',
  'composer.lock',
  'Gemfile.lock',
  '.env',
  '.env.local',
  '.env.production',

  // Binary and media files
  '.png', '.jpg', '.jpeg', '.gif', '.bmp', '.ico', '.svg',
  '.mp3', '.mp4', '.avi', '.mov', '.wav',
  '.zip', '.tar', '.gz', '.rar', '.7z',
  '.pdf', '.doc', '.docx', '.xls', '.xlsx',
  '.exe', '.dll', '.so', '.dylib',
  '.woff', '.woff2', '.ttf', '.otf', '.eot',

  // Compiled files
  '.pyc', '.pyo', '.class', '.o', '.obj',

  // Logs and temp files
  '.log', '.tmp', '.temp', '.swp', '.swo',

  // Test snapshots
  '__snapshots__/',
  '.snap'
];

const SimplePage = () => {
  const [activeTab, setActiveTab] = useState('files');
  const [files, setFiles] = useState([]);
  const [fileTree, setFileTree] = useState(null);
  const [expandedFolders, setExpandedFolders] = useState(new Set());
  const [url, setUrl] = useState('');
  const [isLoadingUrl, setIsLoadingUrl] = useState(false);
  const [context, setContext] = useState('');
  const [copied, setCopied] = useState(false);

  // Check if file should be blacklisted
  const shouldSkipFile = (filePath) => {
    const normalizedPath = filePath.replace(/\\/g, '/');
    return BLACKLIST_PATTERNS.some(pattern => {
      if (pattern.includes('/')) {
        // Directory pattern
        return normalizedPath.includes(pattern);
      } else if (pattern.startsWith('.')) {
        // Extension pattern
        return normalizedPath.endsWith(pattern);
      } else {
        // Filename pattern
        const filename = normalizedPath.split('/').pop();
        return filename === pattern;
      }
    });
  };

  // Build file tree structure
  const buildFileTree = (files) => {
    const tree = { name: 'root', children: {}, files: [] };

    files.forEach(file => {
      const parts = file.path.split('/').filter(p => p);
      let current = tree;

      parts.forEach((part, index) => {
        if (index === parts.length - 1) {
          // It's a file
          current.files.push({ name: part, path: file.path, size: file.size });
        } else {
          // It's a directory
          if (!current.children[part]) {
            current.children[part] = { name: part, children: {}, files: [] };
          }
          current = current.children[part];
        }
      });
    });

    return tree;
  };

  // File processing function
  const processFiles = useCallback(async (fileList) => {
    const textFiles = [];

    for (const file of fileList) {
      const filePath = file.webkitRelativePath || file.name;

      // Skip blacklisted files
      if (shouldSkipFile(filePath)) {
        continue;
      }

      if (file.name.endsWith('.zip')) {
        // Handle zip files
        const zip = new JSZip();
        try {
          const content = await zip.loadAsync(file);
          const promises = [];

          content.forEach((relativePath, zipEntry) => {
            if (!zipEntry.dir && !shouldSkipFile(relativePath) && isTextFile(relativePath)) {
              promises.push(
                zipEntry.async('string').then(text => ({
                  name: relativePath,
                  path: relativePath,
                  content: text,
                  size: text.length
                }))
              );
            }
          });

          const zipFiles = await Promise.all(promises);
          textFiles.push(...zipFiles);
        } catch (error) {
          console.error('Error processing zip:', error);
        }
      } else if (isTextFile(file.name)) {
        const text = await readFileAsText(file);
        textFiles.push({
          name: file.name,
          path: filePath,
          content: text,
          size: file.size
        });
      }
    }

    setFiles(prev => {
      const newFiles = [...prev, ...textFiles];
      updateContext(newFiles);

      // Build file tree if we have files with paths
      if (newFiles.some(f => f.path && f.path.includes('/'))) {
        const tree = buildFileTree(newFiles);
        setFileTree(tree);
      }

      return newFiles;
    });
  }, []);

  // File handling
  const handleFileSelect = useCallback((e) => {
    const selectedFiles = Array.from(e.target.files);
    processFiles(selectedFiles);
  }, [processFiles]);

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    const droppedFiles = Array.from(e.dataTransfer.files);
    processFiles(droppedFiles);
  }, [processFiles]);

  const readFileAsText = (file) => {
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onload = (e) => resolve(e.target.result);
      reader.onerror = () => resolve('Error reading file');
      reader.readAsText(file);
    });
  };

  const isTextFile = (fileName) => {
    const textExtensions = ['txt', 'md', 'json', 'js', 'jsx', 'ts', 'tsx', 'css', 'html', 'xml', 'py', 'java', 'c', 'cpp', 'h', 'cs', 'go', 'rb', 'php', 'sh', 'yaml', 'yml'];
    const ext = fileName.split('.').pop()?.toLowerCase();
    return textExtensions.includes(ext) || fileName.includes('README') || fileName.startsWith('.');
  };

  // URL handling
  const handleUrlFetch = async () => {
    if (!url) return;

    setIsLoadingUrl(true);
    try {
      const proxyUrl = `https://api.allorigins.win/raw?url=${encodeURIComponent(url)}`;
      const response = await fetch(proxyUrl);

      if (!response.ok) throw new Error('Failed to fetch');

      const html = await response.text();
      const tempDiv = document.createElement('div');
      tempDiv.innerHTML = html;
      const text = tempDiv.textContent || tempDiv.innerText || '';

      const cleanText = text.replace(/\s\s+/g, ' ').trim().substring(0, 10000);

      const urlFile = {
        name: new URL(url).hostname,
        content: cleanText,
        size: cleanText.length,
        isUrl: true
      };

      setFiles(prev => {
        const newFiles = [...prev, urlFile];
        updateContext(newFiles);
        return newFiles;
      });
      setUrl('');
    } catch (error) {
      alert('Failed to fetch URL. Please try again.');
    } finally {
      setIsLoadingUrl(false);
    }
  };

  // Context handling
  const updateContext = (fileList) => {
    const contextText = fileList
      .map(file => `## ${file.isUrl ? 'URL' : 'File'}: ${file.path || file.name}\n\n${file.content}\n`)
      .join('\n---\n\n');
    setContext(contextText);
  };

  const removeFile = (index) => {
    const newFiles = files.filter((_, i) => i !== index);
    setFiles(newFiles);
    updateContext(newFiles);
  };

  const clearAll = () => {
    setFiles([]);
    setFileTree(null);
    setExpandedFolders(new Set());
    setContext('');
    setUrl('');
  };

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(context);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      alert('Failed to copy to clipboard');
    }
  };

  const downloadContext = () => {
    const blob = new Blob([context], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'context.txt';
    a.click();
    URL.revokeObjectURL(url);
  };

  // Toggle folder expansion
  const toggleFolder = (path) => {
    const newExpanded = new Set(expandedFolders);
    if (newExpanded.has(path)) {
      newExpanded.delete(path);
    } else {
      newExpanded.add(path);
    }
    setExpandedFolders(newExpanded);
  };

  // Render file tree recursively
  const renderFileTree = (node, path = '') => {
    if (!node) return null;

    const currentPath = path ? `${path}/${node.name}` : node.name;
    const isExpanded = expandedFolders.has(currentPath);

    return (
      <div className="tree-node">
        {/* Render folders */}
        {Object.entries(node.children).map(([name, child]) => {
          const folderPath = path ? `${path}/${name}` : name;
          const isExpanded = expandedFolders.has(folderPath);

          return (
            <div key={folderPath} className="tree-folder">
              <div 
                className="tree-item folder-item"
                onClick={() => toggleFolder(folderPath)}
              >
                {isExpanded ? <ChevronDown size={14} /> : <ChevronRight size={14} />}
                <Folder size={14} />
                <span>{name}</span>
              </div>
              {isExpanded && (
                <div className="tree-children">
                  {renderFileTree(child, folderPath)}
                </div>
              )}
            </div>
          );
        })}

        {/* Render files */}
        {node.files.map(file => (
          <div key={file.path} className="tree-item file-item">
            <FileCode2 size={14} />
            <span>{file.name}</span>
            <span className="file-size">{(file.size / 1024).toFixed(1)} KB</span>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>Context Builder</h1>
        <p className="text-secondary">Prepare content for LLM interactions</p>
      </header>

      <main className="main-content">
        {/* Input Section */}
        <div className="card mb-3">
          <div className="tabs">
            <button 
              className={`tab ${activeTab === 'files' ? 'active' : ''}`}
              onClick={() => setActiveTab('files')}
            >
              <FileCode2 size={16} />
              Files
            </button>
            <button 
              className={`tab ${activeTab === 'url' ? 'active' : ''}`}
              onClick={() => setActiveTab('url')}
            >
              <Globe size={16} />
              URL
            </button>
          </div>

          {activeTab === 'files' ? (
            <div 
              className="drop-zone"
              onDrop={handleDrop}
              onDragOver={(e) => e.preventDefault()}
            >
              <FileCode2 size={32} className="drop-icon" />
              <p>Drop files here or click to select</p>
              <p className="text-secondary">Supports text files and ZIP archives</p>
              <input
                type="file"
                multiple
                webkitdirectory=""
                directory=""
                onChange={handleFileSelect}
                style={{ display: 'none' }}
                id="file-input"
              />
              <label htmlFor="file-input" className="file-input-label">
                Select Files or Folder
              </label>
            </div>
          ) : (
            <div className="url-input">
              <input
                type="url"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="https://example.com"
                onKeyPress={(e) => e.key === 'Enter' && handleUrlFetch()}
              />
              <button 
                onClick={handleUrlFetch} 
                disabled={!url || isLoadingUrl}
              >
                {isLoadingUrl ? 'Fetching...' : 'Fetch'}
              </button>
            </div>
          )}
        </div>

        {/* Files List */}
        {files.length > 0 && (
          <div className="card mb-3">
            <div className="files-header">
              <h2>Sources ({files.length})</h2>
              <button className="secondary" onClick={clearAll}>
                <Trash2 size={16} />
                Clear All
              </button>
            </div>
            <div className="files-list">
              {files.map((file, index) => (
                <div key={index} className="file-item">
                  <span className="file-name">
                    {file.isUrl ? <Globe size={14} /> : <FileCode2 size={14} />}
                    {file.name}
                  </span>
                  <span className="file-size">{(file.size / 1024).toFixed(1)} KB</span>
                  <button 
                    className="icon-button"
                    onClick={() => removeFile(index)}
                  >
                    <Trash2 size={14} />
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Context Output */}
        {context && (
          <div className="card">
            <div className="context-header">
              <h2>Generated Context</h2>
              <div className="context-actions">
                <button onClick={copyToClipboard}>
                  {copied ? <Check size={16} /> : <Copy size={16} />}
                  {copied ? 'Copied!' : 'Copy'}
                </button>
                <button className="secondary" onClick={downloadContext}>
                  <Download size={16} />
                  Download
                </button>
              </div>
            </div>
            <textarea
              value={context}
              readOnly
              className="context-output"
              placeholder="Your generated context will appear here..."
            />
          </div>
        )}
      </main>
    </div>
  );
};

export default SimplePage;
