import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import './index.css'; // Global styles
import Header from './components/Header';
import CodeInputBox from './components/CodeInputBox';
import UrlInputBox from './components/UrlInputBox';
import ContextOutputBox from './components/ContextOutputBox';
import SettingsBox from './components/SettingsBox';
import Footer from './components/Footer';

// Dynamically load Lucide icons
const loadLucide = async () => {
  if (window.lucide) {
    window.lucide.createIcons();
  } else {
    try {
      const lucideModule = await import('lucide-react');
      if (lucideModule && window.lucide) { // Check if lucide added itself to window
          window.lucide.createIcons();
      } else {
          // Fallback or manual setup if direct import doesn't auto-initialize
          // This might be needed if 'lucide-static' isn't the right way to use it in React
          // Or if using 'lucide-react' components directly is preferred.
          // For now, assuming the script in index.html still works or we adjust.
          console.warn('Lucide script might not be loaded or initialized correctly for dynamic icons after initial page load.');
      }
    } catch (error) {
      console.error("Failed to load lucide-react:", error);
    }
  }
};

function App() {
  const [contextText, setContextText] = useState('');
  const [lastUrl, setLastUrl] = useState('');
  const [processedFiles, setProcessedFiles] = useState([]); // To keep track of files added

  useEffect(() => {
    const scriptId = 'lucide-script';
    let script = document.getElementById(scriptId);
    if (!script) {
        script = document.createElement('script');
        script.id = scriptId;
        script.src = "https://unpkg.com/lucide-static@latest/dist/lucide.min.js";
        script.async = true;
        script.onload = () => {
          if (window.lucide) {
            window.lucide.createIcons();
          }
        };
        document.body.appendChild(script);
    } else if (window.lucide) {
        window.lucide.createIcons(); // If script already loaded, just run createIcons
    }

    // No cleanup needed for script if it's meant to persist across App mounts/unmounts
    // If App can unmount and remount, a more robust script management might be needed.
  }, []);

  // General effect to refresh icons when major state changes occur
  useEffect(() => {
    if (window.lucide) {
      window.lucide.createIcons();
    }
  }, [contextText, processedFiles, lastUrl]); // Add dependencies that might add new icons

  const handleUrlFetched = useCallback((content, url) => {
    setContextText(prevText => prevText + content + '\n\n');
    setLastUrl(url);
  }, []);

  const handleFilesProcessed = useCallback((filesContent, newFiles) => {
    // This is a placeholder. Actual file content processing will be more complex.
    // For now, just appending a note about the files.
    const fileNotes = newFiles.map(f => `Processed file: ${f.name}`).join('\n');
    setContextText(prevText => prevText + fileNotes + '\n\n');
    setProcessedFiles(prevFiles => [...prevFiles, ...newFiles.map(f => f.name)]);
  }, []);

  const handleClearContext = useCallback(() => {
    setContextText('');
    setLastUrl('');
    setProcessedFiles([]);
    // Potentially reset CodeInputBox files state too, if it doesn't clear itself.
    // This would require passing a reset trigger or managing its files state in App.js
  }, []);

  return (
    <div className="container">
      <Header />
      <main className="bento-grid">
        <CodeInputBox onFilesProcessed={handleFilesProcessed} />
        <UrlInputBox onUrlFetched={handleUrlFetched} />
        <ContextOutputBox contextText={contextText} onClearContext={handleClearContext} />
        <SettingsBox />
      </main>
      <Footer />
    </div>
  );
}

export default App;
