import React, { useState, useCallback, useEffect } from 'react';
import './CodeInputBox.css';

const CodeInputBox = ({ onFilesProcessed }) => {
  const [files, setFiles] = useState([]);
  const [isDragOver, setIsDragOver] = useState(false);

  const handleFileChange = (event) => {
    handleFiles(event.target.files);
  };

  const handleFiles = useCallback((fileList) => {
    if (fileList.length > 0) {
      const newFiles = Array.from(fileList).map(file => ({
        name: file.name,
        size: file.size,
        // actual file object might be needed for processing later
        // rawFile: file 
      }));
      setFiles(prevFiles => [...prevFiles, ...newFiles]);
      onFilesProcessed(`Content from ${newFiles.length} files processed.`, newFiles);
    }
  }, [onFilesProcessed]);

  const handleDrop = useCallback((event) => {
    event.preventDefault();
    event.stopPropagation();
    setIsDragOver(false);
    handleFiles(event.dataTransfer.files);
  }, [handleFiles]);

  const handleDragOver = useCallback((event) => {
    event.preventDefault();
    event.stopPropagation();
    setIsDragOver(true);
  }, []);

  const handleDragLeave = useCallback((event) => {
    event.preventDefault();
    event.stopPropagation();
    setIsDragOver(false);
  }, []);

  // Effect to call lucide.createIcons() when files change, so new icons are rendered
  useEffect(() => {
    if (window.lucide) {
      window.lucide.createIcons();
    }
  }, [files]);

  return (
    <div className="bento-box code-input-box">
      <div className="box-header">
        <i data-lucide="file-code-2"></i>
        <h2>Code Processor</h2>
      </div>
      <p className="box-description">Upload code files, folders, or a ZIP archive.</p>
      <div 
        className={`file-drop-area ${isDragOver ? 'dragover' : ''}`}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
      >
        <i data-lucide="upload-cloud" className="upload-icon"></i>
        <p>Drag & drop files here</p>
        <p className="or-text">or</p>
        <label htmlFor="file-upload-input" className="custom-file-upload">
          <i data-lucide="file-plus"></i> Choose Files
        </label>
        <input 
          type="file" 
          id="file-upload-input" 
          multiple 
        //   webkitdirectory="true" // Note: webkitdirectory is non-standard
        //   directory="true" // Note: directory is non-standard
          style={{ display: 'none' }} 
          onChange={handleFileChange}
        />
      </div>
      {files.length > 0 && (
        <div className="file-list-preview">
          <h4>Selected Files:</h4>
          <ul>
            {files.map((file, index) => (
              <li key={index}>
                {file.name} ({(file.size / 1024).toFixed(2)} KB)
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default CodeInputBox;
