import React, { useState, useEffect } from 'react';
import './UrlInputBox.css';

const UrlInputBox = ({ onUrlFetched }) => {
  const [url, setUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleFetchUrl = () => {
    if (url) {
      setIsLoading(true);
      // Simulate network request
      setTimeout(() => {
        setIsLoading(false);
        const fetchedContent = `Fetched content from: ${url}\n\n(Actual content fetching needs JavaScript implementation beyond this demo.)`;
        onUrlFetched(fetchedContent, url); // Pass content and URL back to App
        // setUrl(''); // Optionally clear URL input after fetch
      }, 2000);
    } else {
      alert("Please enter a URL.");
    }
  };

  // Effect to call lucide.createIcons() if icons are added/changed dynamically
  useEffect(() => {
    if (window.lucide) {
      window.lucide.createIcons();
    }
  }, [isLoading]);

  return (
    <div className="bento-box url-input-box">
      <div className="box-header">
        <i data-lucide="globe"></i>
        <h2>Website Scraper</h2>
      </div>
      <p className="box-description">Enter a URL to fetch its content.</p>
      <div className="url-input-group">
        <input 
          type="url" 
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="https://example.com"
          disabled={isLoading}
        />
        <button onClick={handleFetchUrl} disabled={isLoading || !url}>
          {isLoading ? <i className="loading-icon-animate"></i> : <i data-lucide="arrow-right-circle"></i>}
          {isLoading ? 'Fetching...' : 'Fetch'}
        </button>
      </div>
      {isLoading && (
        <div className="loading-indicator">
          <div className="spinner"></div>
          <p>Fetching content...</p>
        </div>
      )}
    </div>
  );
};

export default UrlInputBox;
