import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import './index.css'; // Global styles
import Header from './components/Header';
import CodeInputBox from './components/CodeInputBox';
import UrlInputBox from './components/UrlInputBox';
import ContextOutputBox from './components/ContextOutputBox';
import SettingsBox from './components/SettingsBox';
import Footer from './components/Footer';

// All lucide script loading and direct window.lucide calls should be removed.
// Icons will be imported from lucide-react into each component directly.

function App() {
  const [contextText, setContextText] = useState('');
  const [lastUrl, setLastUrl] = useState('');
  const [processedFilesInfo, setProcessedFilesInfo] = useState([]);

  const handleUrlFetched = useCallback((content, url) => {
    setContextText(prevText => prevText + `## Content from URL: ${url}\n\n${content}\n\n`);
    setLastUrl(url);
  }, []);

  const handleFilesProcessed = useCallback((fileContents) => {
    let newContext = '';
    const newFilesInfo = [];
    fileContents.forEach(file => {
      newContext += `## Content from File: ${file.name}\n\n${file.content}\n\n`;
      newFilesInfo.push({name: file.name, type: file.type, size: file.size});
    });
    setContextText(prevText => prevText + newContext);
    setProcessedFilesInfo(prevFiles => [...prevFiles, ...newFilesInfo]);
  }, []);

  const handleClearContext = useCallback(() => {
    setContextText('');
    setLastUrl('');
    setProcessedFilesInfo([]);
  }, []);

  return (
    <div className="container">
      <Header />
      <main className="bento-grid">
        {/* Pass a key to CodeInputBox derived from processedFilesInfo to force re-render and clear its internal state on clear context */}
        <CodeInputBox onFilesProcessed={handleFilesProcessed} key={processedFilesInfo.map(f => f.name).join('-')} />
        <UrlInputBox onUrlFetched={handleUrlFetched} />
        <ContextOutputBox contextText={contextText} onClearContext={handleClearContext} />
        <SettingsBox />
      </main>
      <Footer />
    </div>
  );
}

export default App;
