import React, { useEffect } from 'react';
import './ContextOutputBox.css';

const ContextOutputBox = ({ contextText, onClearContext }) => {

  const handleCopyContext = () => {
    if (contextText) {
      navigator.clipboard.writeText(contextText)
        .then(() => alert("Context copied to clipboard!"))
        .catch(err => console.error('Failed to copy: ', err));
    } else {
      alert("Nothing to copy.");
    }
  };

  // Effect to call lucide.createIcons() if icons are added/changed dynamically
  useEffect(() => {
    if (window.lucide) {
      window.lucide.createIcons();
    }
  }); // Re-run on every render as button states might change visibility of icons

  return (
    <div className="bento-box context-output-box">
      <div className="box-header">
        <i data-lucide="clipboard-check"></i>
        <h2>Generated Context</h2>
      </div>
      <div className="context-controls">
        <button id="copy-context-button" onClick={handleCopyContext} disabled={!contextText}>
          <i data-lucide="copy"></i> Copy Context
        </button>
        <button id="clear-context-button" className="secondary" onClick={onClearContext} disabled={!contextText}>
          <i data-lucide="trash-2"></i> Clear
        </button>
      </div>
      <textarea 
        id="context-display" 
        value={contextText}
        placeholder="Processed context will appear here..." 
        readOnly
      ></textarea>
    </div>
  );
};

export default ContextOutputBox;
